void afterNodeAccess(Node e) { // move node to last
    LinkedHashMap.Entry last;
    // 如果是按照访问顺序，那么把节点e交换到尾节点
    if (accessOrder && (last = tail) != e) {
        LinkedHashMap.Entry p =
            (LinkedHashMap.Entry)e, b = p.before, a = p.after;
        p.after = null;
        if (b == null)
