static  TreeMap.Entry successor(Entry t) {
    // 首先明确，下个节点是比当前节点大的节点，为当前节点右节点的左叶子节点
    if (t == null)
        return null;
    else if (t.right != null) {
        Entry p = t.right;
        while (p.left != null)
            p = p.left;
        return p;
    } else {
        Entry p = t.parent;
        Entry ch = t;
        // 当右节点为空，并且是父节点的右节点时，下个节点当前分支树的父节点
        while (p != null && ch == p.right) {
            ch = p;
            p = p.parent;
        }
        // 当右节点为空，并且是父节点的左节点时，下个节点当前节点的父节点
        return p;
    }
}

